// Create a new image\n$image = imagecreate(400, 200);\n\n// Set the background color\n$background_color = imagecolorallocate($image, 255, 255, 255);\nimagefill($image, 0, 0, $background_color);\n\n// Set the text color\n$text_color = imagecolorallocate($image, 0, 0, 0);\n\n// Set the font size and style\n$font_size = 24;\n$font_style = 'arial.ttf';\n\n// Set the text to be displayed\n$text = 'Hello, World!';\n\n// Calculate the x and y coordinates for the text\n$x = 10;\n$y = 50;\n\n// Draw the text\nimagettftext($image, $font_size, 0, $x, $y, $text_color, $font_style, $text);\n\n// Draw the underline\n$underline_color = imagecolorallocate($image, 0, 0, 0);\nimagesetthickness($image, 2);\nimageline($image, $x, $y + 2, $x + strlen($text) * $font_size / 2, $y + 2, $underline_color);\n\n// Output the image\nheader('Content-Type: image/png');\nimagepng($image);\nimagedestroy($image);