// PhoneNumbers class\nclass PhoneNumbers {\n    private $numbers;\n\n    public function __construct(array $numbers) {\n        $this->numbers = $numbers;\n    }\n\n    public function getNumbers() {\n        return $this->numbers;\n    }\n}\n\n// CampaignSelection class\nclass CampaignSelection {\n    private $campaigns;\n\n    public function __construct(array $campaigns) {\n        $this->campaigns = $campaigns;\n    }\n\n    public function getCampaigns() {\n        return $this->campaigns;\n    }\n}\n\n// ... similar classes for Contacts, Groups, User, Text, and TotalQuantity ...\n\n// CostCountingData class, now composed of smaller objects\nclass CostCountingData {\n    private $phoneNumbers;\n    private $campaignSelection;\n    private $contacts;\n    private $groups;\n    private $user;\n    private $text;\n    private $totalQuantity;\n\n    public function __construct(\n        PhoneNumbers $phoneNumbers,\n        CampaignSelection $campaignSelection,\n        Contacts $contacts,\n        Groups $groups,\n        User $user,\n        Text $text,\n        TotalQuantity $totalQuantity\n    ) {\n        $this->phoneNumbers = $phoneNumbers;\n        $this->campaignSelection = $campaignSelection;\n        $this->contacts = $contacts;\n        $this->groups = $groups;\n        $this->user = $user;\n        $this->text = $text;\n        $this->totalQuantity = $totalQuantity;\n    }\n\n    // getters for each component\n}