$versionDir = 'sitemap-versions';\nif (!file_exists($versionDir)) {\n    mkdir($versionDir, 0777, true);\n}\n\n// Store the current version\n$versionFile = $versionDir . '/sitemap-v' . date('YmdHis') . '.xml';\nfile_put_contents($versionFile, $sitemapXML->asXML());\n\n// Remove old versions\n$files = glob($versionDir . '/sitemap-v*.xml');\nif (count($files) > 3) {\n    $oldFiles = array_slice($files, 0, -3);\n    foreach ($oldFiles as $oldFile) {\n        unlink($oldFile);\n    }\n}