// Assuming $file_path is the path to your uploaded file\n// and $mysqli is your MySQLi connection object\n\n$file_path = $_FILES['your_file_input_name']['tmp_name'];\n$file_size = filesize($file_path);\n\n// Open the file for reading in binary mode\n$fp = fopen($file_path, 'rb');\n\n// Create a prepared statement\n$stmt = $mysqli->prepare("INSERT INTO your_table (file_data) VALUES (?)");\n\n// Bind the parameter as a blob\n$null = NULL;\n$stmt->bind_param("b", $null);\n\n// Send the blob data in chunks\nwhile (!feof($fp)) {\n    $chunk = fread($fp, 8192); // Read 8KB at a time\n    $stmt->send_long_data(0, $chunk);\n}\n\n// Close the file\nfclose($fp);\n\n// Execute the query\n$stmt->execute();\n\n// Close the statement\n$stmt->close();