$url = 'ctx_ver=Z39.88-2004&rft_id=info:oclcnum/1903126&rft_id=http://www.biodiversitylibrary.org/bibliography/4323&...';\n\n$query = parse_url($url, PHP_URL_QUERY);\n$params = explode('&', $query);\n$rft_id_values = array();\n\nforeach ($params as $param) {\n    list($key, $value) = explode('=', $param);\n    if ($key == 'rft_id') {\n        $rft_id_values[] = $value;\n    }\n}\n\nprint_r($rft_id_values);