$password = "ThisIsMyKey";\n$encodedData = base64_decode($p_data, true);\n$iv = base64_decode($p_iv, true);\n\n$td = mcrypt_module_open(MCRYPT_BLOWFISH, '', MCRYPT_MODE_CBC, '');\n$keySize = mcrypt_enc_get_key_size($td);\n$iterations = 1;\n\n$key = hash_pbkdf2('md5', $password, '', $iterations, $keySize, true);\n\nmcrypt_generic_init($td, $key, $iv);\n$decodedData = mdecrypt_generic($td, $encodedData);\nmcrypt_generic_deinit($td);\nmcrypt_module_close($td);\n\n// Remove PKCS#7 padding\n$paddingSize = ord($decodedData[strlen($decodedData) - 1]);\n$decodedData = substr($decodedData, 0, -$paddingSize);\n\necho "decoded: " . $decodedData;