$sql = 'SELECT photographers.photographer_id, photographers.photographer_name\n        FROM photographers';\n\n$stmt = $conn->prepare($sql);\nif ($stmt->execute()) {\n    $result = $stmt->get_result();\n    $photographers = array();\n    while ($row = $result->fetch_assoc()) {\n        $photographers[] = $row;\n    }\n    $stmt->close();\n} else {\n    // handle error\n}\n\n// Now you can loop through the $photographers array\nforeach ($photographers as $photographer) {\n    echo $photographer['photographer_id'] . ' ' . $photographer['photographer_name'] . "\n";\n}