function addStatusMsg($msg, $type = 'info') {\n    if (!isset($_SESSION['status_msgs'])) {\n        $_SESSION['status_msgs'] = array();\n    }\n    $_SESSION['status_msgs'][] = array('msg' => $msg, 'type' => $type);\n}\n\nfunction displayStatusMsg() {\n    if (isset($_SESSION['status_msgs'])) {\n        $msgs = $_SESSION['status_msgs'];\n        unset($_SESSION['status_msgs']); // clear the array\n        $output = '';\n        foreach ($msgs as $msg) {\n            $output .= '<div class="' . $msg['type'] . '">' . $msg['msg'] . '</div>';\n        }\n        return $output;\n    }\n    return '';\n}