use SebastianBergmann\Diff\Differ;\n\n$string1 = 'This is the original string';\n$string2 = 'This is the modified string';\n\n$differ = new Differ();\n$diff = $differ->diffToArray($string1, $string2);\n\nforeach ($diff as $line) {\n    if ($line[1] === 0) {\n        echo '  ' . $line[0] . "\n"; // unchanged line\n    } elseif ($line[1] === 1) {\n        echo '<ins>' . $line[0] . '</ins>' . "\n"; // added line\n    } elseif ($line[1] === 2) {\n        echo '<del>' . $line[0] . '</del>' . "\n"; // removed line\n    }\n}