$apubs_final = array();\n\nforeach ($apubs as $arec) {\n    if (!isset($apubs_final[$arec['spubid']])) {\n        $apubs_final[$arec['spubid']] = $arec;\n        $apubs_final[$arec['spubid']]['authors'] = array();\n    }\n    \n    $author = array(\n        'sfirst' => $arec['sfirst'],\n        'smi' => $arec['smi'],\n        'slast' => $arec['slast']\n    );\n    \n    $apubs_final[$arec['spubid']]['authors'][] = $author;\n}\n\n// reset array keys\n$apubs_final = array_values($apubs_final);