class Animal {\n  protected $id;\n  protected $legs;\n  protected $height;\n\n  private function __construct($id) {}\n\n  public static function create($type, $id) {\n    if ($type == "dog") {\n      return new Dog($id);\n    } elseif ($type == "cat") {\n      return new Cat($id);\n    } elseif ($type == "mouse") {\n      return new Mouse($id);\n    } else {\n      throw new Exception("Invalid animal type");\n    }\n  }\n}\n\nclass Dog extends Animal {\n  public function __construct($id) {\n    $this->id = $id;\n    $this->legs = 4;\n    $this->height = 1;\n  }\n}\n\n// Usage:\n$animal = Animal::create("dog", 12);