// Generate a random session ID\n$sessionID = bin2hex(random_bytes(16));\n\n// Store the session ID in the database\n$stmt = $pdo->prepare("INSERT INTO sessions (sessionID, userID, token, expires) VALUES (:sessionID, :userID, :token, :expires)");\n$stmt->execute([\n    "sessionID" => $sessionID,\n    "userID" => $userID,\n    "token" => $token,\n    "expires" => time() + 1209600 // 2 weeks\n]);\n\n// Set the cookie\nsetcookie("remember_me", $sessionID . ":" . $token, time() + 1209600, "/");\n\n// Verify the cookie on subsequent visits\nif (isset($_COOKIE["remember_me"])) {\n    list($sessionID, $token) = explode(":", $_COOKIE["remember_me"]);\n    $stmt = $pdo->prepare("SELECT * FROM sessions WHERE sessionID = :sessionID AND token = :token AND expires > NOW()");\n    $stmt->execute([\n        "sessionID" => $sessionID,\n        "token" => $token\n    ]);\n    if ($stmt->fetch()) {\n        // Authenticate the user\n    } else {\n        // Invalid cookie, delete it\n        setcookie("remember_me", "", time() - 3600, "/");\n    }\n}