// Assume you have a form with id 'form1'\n$form->create('Model', array('id' => 'form1', 'action' => 'main_action'));\n\n// Inside the form, create a table with rows\n<table>\n  <tr>\n    <td>\n      <?php echo $this->Form->button('Submit Child Form', array('id' => 'child-form-submit')); ?>\n    </td>\n  </tr>\n</table>\n\n// Use JavaScript to change the form's action and submit the form\n<?php $this->Js->buffer("\n  $('#child-form-submit').click(function(event) {\n    event.preventDefault();\n    var childFormAction = '/model/child_action/' + $(this).data('row-id');\n    $('#form1').attr('action', childFormAction);\n    $('#form1').submit();\n  });\n"); ?>