<?php\n\n$finder = PhpCsFixer\Finder::create()\n    ->in(__DIR__)\n    ->name('*.php')\n    ->notName('*.twig.php')\n    ->notName('*.tpl.php')\n    ->ignoreDotFiles(true)\n    ->ignoreVCS(true)\n;\n\n$fixers = [\n    'indentation',\n    'line_ending',\n    'multiple_escaped_heredocs',\n    'multiple_variadic_arguments',\n    'no_closing_tag',\n    'no_short_bool_cast',\n    'no_unused_imports',\n    'ordered_imports',\n    'phpdoc_indent',\n    'phpdoc_inline_tag',\n    'phpdoc_no_access',\n    'phpdoc_no_package',\n    'phpdoc_scalar',\n    'phpdoc_separation',\n    'phpdoc_single_line_var_spacing',\n    'phpdoc_summary',\n    'phpdoc_to_comment',\n    'phpdoc_trim',\n    'phpdoc_var_without_name',\n    'short_scalar_cast',\n    'single_blank_line_before_namespace',\n    'single_quote',\n    'spaces_after_semicolon',\n    'spaces_inside_parenthesis',\n    'standardize_not_equal',\n    'ternary_operator_spaces',\n    'trim_array_spaces',\n    'unary_operator_spaces',\n    'visibility_required',\n    'whitespace_after_comma_in_array',\n];\n\n$config = PhpCsFixer\Config::create()\n    ->setRules($fixers)\n    ->setFinder($finder)\n    ->setCacheManager(new PhpCsFixer\Cache\Manager(new PhpCsFixer\Cache\ChainCacheManager([\n        new PhpCsFixer\Cache\ApcuCacheManager(),\n        new PhpCsFixer\Cache\PhpFileCacheManager(__DIR__ . '/.php_cs.cache'),\n    ])))\n;\n\nreturn $config;