function autoload_function($function_name) {\n    $prefix = substr($function_name, 0, strpos($function_name, '_'));\n    $file_name = $prefix . '_functions.php';\n    if (file_exists($file_name)) {\n        require_once $file_name;\n    }\n}\n\nregister_shutdown_function('autoload_function_handler');\n\nfunction autoload_function_handler() {\n    $error = error_get_last();\n    if ($error['type'] == E_ERROR && strpos($error['message'], 'undefined function') !== false) {\n        $function_name = substr($error['message'], strpos($error['message'], ' ') + 1);\n        autoload_function($function_name);\n    }\n}