$xml = simplexml_load_string($response->body->asXML());\n$items = $xml->xpath('//Item');\n\n$array = array();\nforeach ($items as $item) {\n    $itemArray = array();\n    $itemArray['name'] = (string) $item->Name;\n\n    $attributes = $item->Attribute;\n    foreach ($attributes as $attribute) {\n        $itemArray[(string) $attribute->Name] = (string) $attribute->Value;\n    }\n\n    $array[] = $itemArray;\n}