// example migration script for Phinx\nuse Phinx\Migration\AbstractMigration;\n\nclass MyMigration extends AbstractMigration\n{\n    public function up()\n    {\n        $table = $this->table('mytable');\n        $table->addColumn('mycolumn', 'string')\n              ->save();\n    }\n\n    public function down()\n    {\n        $table = $this->table('mytable');\n        $table->removeColumn('mycolumn')\n              ->save();\n    }\n}