<?php\n// process.php\n\n// Input validation\nif (!isset($_POST['action']) || empty($_POST['action'])) {\n    http_response_code(400);\n    exit('Invalid request');\n}\n\n// Authentication and authorization\nif (!isset($_SESSION['user']) || !$_SESSION['user']->hasPermission('perform_action')) {\n    http_response_code(403);\n    exit('Forbidden');\n}\n\n// Dispatch action\n$action = $_POST['action'];\n$businessClass = new BusinessClass();\n$businessClass->performAction($action);\n\n// ...