$content = new DOMDocument();\n$content->loadHTMLFile('content.html');\n$story = $content->getElementById('story');\n$topics = $story->getElementsByTagName('div');\nforeach ($topics as $topic) {\n    if ($topic->getAttribute('class') === 'topic') {\n        $topicContents = $topic->childNodes;\n        foreach ($topicContents as $contentElement) {\n            if ($contentElement instanceof DOMElement) {\n                echo $content->saveXML($contentElement);\n            }\n        }\n    }\n}