<?php\n// Connect to MySQL database\n$conn = new mysqli('localhost', 'username', 'password', 'database');\n\n// Check connection\nif ($conn->connect_error) {\n  die("Connection failed: " . $conn->connect_error);\n}\n\n// Get the selected service from the GET request\n$service = $_GET['service'];\n\n// Query the database to fetch the price for the selected service\n$query = "SELECT price FROM services WHERE name = '$service'";\n$result = $conn->query($query);\n\n// Fetch the price from the result\n$price = $result->fetch_assoc()['price'];\n\n// Return the price as JSON\nheader('Content-Type: application/json');\necho json_encode(['price' => $price]);\n\n// Close the database connection\n$conn->close();\n?>