function generatePyramidStrengths($numPlayers, $minStrength, $maxStrength) {\n    $strengths = array();\n    for ($i = 0; $i < $numPlayers; $i++) {\n        $strength = mt_rand($minStrength * 100, $maxStrength * 100) / 100;\n        $strengths[] = $strength;\n    }\n    // Apply the power function to bias the distribution\n    foreach ($strengths as &$strength) {\n        $strength = pow($strength, 2) / $maxStrength;\n    }\n    return $strengths;\n}\n\n// Example usage:\n$numPlayers = 50000;\n$minStrength = 1.1;\n$maxStrength = 9.9;\n$strengths = generatePyramidStrengths($numPlayers, $minStrength, $maxStrength);\n\n// Print the results\n$histogram = array();\nforeach ($strengths as $strength) {\n    $bucket = floor($strength);\n    if (!isset($histogram[$bucket])) {\n        $histogram[$bucket] = 0;\n    }\n    $histogram[$bucket]++;\n}\nksort($histogram);\nprint_r($histogram);