<?php\n// Connect to database\n$db = new mysqli('host', 'username', 'password', 'database');\n\n// Retrieve new user data\n$newData = $db->query('SELECT * FROM user_data WHERE processed = 0');\n\n// Update benchmark values\n$benchmarkValues = calculateBenchmarkValues($newData);\n$db->query('UPDATE benchmark_values SET mean = ?, std_dev = ?', $benchmarkValues['mean'], $benchmarkValues['std_dev']);\n\n// Mark new user data as processed\n$db->query('UPDATE user_data SET processed = 1 WHERE processed = 0');