use phpseclib3\Zip\Zip;\nuse phpseclib3\Zip\ZipArchive;\n\n// Create a new zip archive\n$zip = new Zip();\n\n// Add a file to the archive\n$zip->addFile('path/to/your/file.txt', 'file.txt');\n\n// Set the password for the archive\n$zip->setPassword('your_secure_password');\n\n// Set the encryption algorithm to AES-256\n$zip->setEncryptionMethod(Zip::ENCRYPTION_METHOD_AES256);\n\n// Save the archive to a file\n$zip->writeFile('encrypted_archive.zip');