function limit_string($str, $limit) {\n    if (strlen($str) > $limit) {\n        return substr($str, 0, $limit) . '...';\n    } else {\n        return $str;\n    }\n}\n\n$string = '12345678901234567890abcdefg';\n$limited_string = limit_string($string, 25);\necho $limited_string; // Outputs: 12345678901234567890abcde...