<?php\n$monthlybill = filter_var($_GET['monthlybill'], FILTER_VALIDATE_FLOAT);\nif ($monthlybill === false) {\n    // handle invalid input\n    exit;\n}\n\n// values.php should be included securely using require_once\nrequire_once 'values.php';\n\n$offers = array(\n    'offer1' => array('name' => 'Offer 1', 'logo' => 'logo1.png', 'link' => 'link1', 'anchor' => 'anchor1', 'star' => 'star1.png', 'bottomline' => 'bottomline1', 'calcsavings' => $offer1calcsavings),\n    'offer2' => array('name' => 'Offer 2', 'logo' => 'logo2.png', 'link' => 'link2', 'anchor' => 'anchor2', 'star' => 'star2.png', 'bottomline' => 'bottomline2', 'calcsavings' => $offer2calcsavings),\n    // ...\n    'offer7' => array('name' => 'Offer 7', 'logo' => 'logo7.png', 'link' => 'link7', 'anchor' => 'anchor7', 'star' => 'star7.png', 'bottomline' => 'bottomline7', 'calcsavings' => $offer7calcsavings),\n);\n\necho '<table>';\nforeach ($offers as $offer) {\n    $savings = $monthlybill - $offer['calcsavings'];\n    echo '<tr>\n        <td><a href="' . $offer['link'] . '" target="blank"><img src="http://www.nextadvisor.com' . $offer['logo'] . '" alt="' . $offer['name'] . '" /></a></td>\n        <td><span class="rating_text">Rating:</span> <span class="star_rating1"><img src="http://www.nextadvisor.com' . $offer['star'] . '" alt="" /></span><br />\n            <div style="margin-top:5px; color:#0000FF;">\n                <a href="' . $offer['link'] . '" target="blank">Go to Site</a>\n                <span style="margin:0px 7px 0px 7px;">|</span>\n                <a href="' . $offer['anchor'] . '">Review</a>\n            </div>\n        </td>\n        <td>' . number_format($savings, 2) . '</td>\n    </tr>';\n}\necho '</table>';