function buildTree($array) {\n    $tree = array();\n    foreach ($array as $item) {\n        if ($item['ParentID'] == 0) {\n            $tree[] = $item;\n        } else {\n            $parent = findParent($tree, $item['ParentID']);\n            if ($parent) {\n                if (!isset($parent['Children'])) {\n                    $parent['Children'] = array();\n                }\n                $parent['Children'][] = $item;\n            }\n        }\n    }\n    return $tree;\n}\n\nfunction findParent($array, $parentId) {\n    foreach ($array as &$item) {\n        if ($item['CategoryID'] == $parentId) {\n            return $item;\n        }\n        if (isset($item['Children'])) {\n            $parent = findParent($item['Children'], $parentId);\n            if ($parent) {\n                return $parent;\n            }\n        }\n    }\n    return null;\n}\n\n$array = /* your array here */;\n$tree = buildTree($array);\nprint_r($tree);