// Create a new file in application/hooks directory, e.g., ErrorEmailer.php\n\nclass ErrorEmailer {\n    public function send_error_email($severity, $message, $filepath, $line) {\n        // Set your email settings\n        $to = 'your_email@example.com';\n        $from = 'your_from_email@example.com';\n        $subject = 'Error Alert: ' . $severity . ' - ' . $message;\n\n        // Get the error message\n        $error_message = date('Y-m-d H:i:s') . ' - ' . $severity . ' - ' . $message . ' in ' . $filepath . ' on line ' . $line;\n\n        // Send the email\n        $this->CI =& get_instance();\n        $this->CI->load->library('email');\n        $this->CI->email->from($from);\n        $this->CI->email->to($to);\n        $this->CI->email->subject($subject);\n        $this->CI->email->message($error_message);\n        $this->CI->email->send();\n    }\n}