$keys = array();\n$values = array();\n\n$lines = file('english.php');\nforeach($lines as $line) {\n    $matches=array();\n    if (preg_match('/DEFINE\(\'(.*?)\',\s*\'(.*)\'\);/i', $line, $matches)) {\n        $keys[] = $matches[1];\n        $values[] = $matches[2];\n    }\n}\n\nif (!empty($keys) && !empty($values)) {\n    $lang = array_combine($keys, $values);\n} else {\n    $lang = array(); // or handle the case where no matches were found\n}