// Limit the files that can be accessed\n$allowedFiles = array('file1.txt', 'file2.pdf');\n\nif (in_array($_GET['file'], $allowedFiles)) {\n    // Ensure the file exists and is readable\n    $filePath = '/path/to/files/' . $_GET['file'];\n    if (file_exists($filePath) && is_readable($filePath)) {\n        // Read and output the file securely\n        readfile($filePath);\n    } else {\n        echo "File not found or not readable.";\n    }\n} else {\n    echo "Access denied.";\n}