// Create a new cursor and execute a query with OCI_NO_AUTO_COMMIT\n$stid = oci_parse($conn, 'BEGIN my_transaction; END;');\noci_execute($stid, OCI_NO_AUTO_COMMIT);\n$cursor = oci_new_cursor($conn);\noci_execute($cursor, OCI_NO_AUTO_COMMIT);\n\n// Store the cursor and connection handles in a session or cache\n$_SESSION['cursor'] = $cursor;\n$_SESSION['conn'] = $conn;\n\n// In subsequent requests, retrieve the cursor and connection handles\n$cursor = $_SESSION['cursor'];\n$conn = $_SESSION['conn'];\n\n// Continue executing queries with OCI_NO_AUTO_COMMIT\n$stid = oci_parse($conn, 'INSERT INTO my_table VALUES (:value)');\noci_bind_by_name($stid, ':value', $value);\noci_execute($stid, OCI_NO_AUTO_COMMIT);\n\n// Finally, commit the transaction when the user presses the "save" button\n$cursor = $_SESSION['cursor'];\n$conn = $_SESSION['conn'];\noci_commit($conn);\noci_free_statement($cursor);\noci_close($conn);