$dsn = 'mysql:host=localhost;dbname=mydatabase';\n$username = 'myusername';\n$password = 'mypassword';\n\n$pdo = new PDO($dsn, $username, $password);\n\n$name = 'John Doe';\n$image = 'image.jpg';\n$descrip = 'This is a description';\n\n$query = "INSERT INTO pat_table (pat_name, pat_image, pat_descrip, pat_doctor, pat_resident, pat_create, pat_modify) VALUES (:name, :image, :descrip, :pat_doctor, :pat_resident, :pat_create, :pat_modify)";\n\n$stmt = $pdo->prepare($query);\n\n$stmt->bindParam(':name', $name);\n$stmt->bindParam(':image', $image);\n$stmt->bindParam(':descrip', $descrip);\n$stmt->bindParam(':pat_doctor', 0);\n$stmt->bindParam(':pat_resident', 0);\n$stmt->bindParam(':pat_create', date("Ymd"));\n$stmt->bindParam(':pat_modify', date("Ymd"));\n\n$stmt->execute();