function input_lab_results($name, $image, $descrip) {\n    $query = "INSERT INTO pat_table (pat_name, pat_image, pat_descrip, pat_doctor, pat_resident, pat_create, pat_modify) VALUES (?, ?, ?, ?, ?, ?, ?)";\n\n    $pat_doctor = 0;\n    $pat_resident = 0;\n    $pat_create = date("Ymd");\n    $pat_modify = date("Ymd");\n\n    if($stmt = $this->conn->prepare($query)){\n        $stmt->bind_param('sssiidd', $name, $image, $descrip, $pat_doctor, $pat_resident, $pat_create, $pat_modify);\n        $stmt->execute();\n        die();\n    } else{\n        die($this->conn->error);\n    }\n}