function validate_youtube_script($script) {\n    $pattern = '/^<iframe\s+width="[\d]+"\s+height="[\d]+"\s+src="https:\/\/www\.youtube\.com\/embed\/[a-zA-Z0-9_-]+"(\s+frameborder="[\d]+")?\s+(allowfullscreen)?"><\/iframe>$/';\n    if (preg_match($pattern, $script)) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\n// Example usage\n$script = '<iframe width="560" height="315" src="https://www.youtube.com/embed/VIDEO_ID" frameborder="0" allowfullscreen></iframe>';\nif (validate_youtube_script($script)) {\n    echo "Valid YouTube script";\n} else {\n    echo "Invalid YouTube script";\n}