class Application_Plugin_InitPlugin extends Zend_Controller_Plugin_Abstract\n{\n    public function preDispatch(Zend_Controller_Request_Abstract $request)\n    {\n        $view = Zend_Layout::getMvcInstance()->getView();\n        $view->user = Zend_Auth::getInstance()->getIdentity();\n        $view->siteName = Zend_Registry::get('config')->site->name;\n        $view->menu = Zend_Controller_Action_HelperBroker::getHelper('generateMenu')->generateMenu(Zend_Auth::getInstance()->getIdentity());\n        $view->slogan = Zend_Registry::get('config')->site->slogan;\n    }\n}