function overwriteLineInFile($filePath, $targetString, $replacementString) {\n    // Load the file into an array\n    $lines = file($filePath, FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n\n    if ($lines === false) {\n        throw new Exception("Failed to read the file.");\n    }\n\n    // Iterate over the lines to find and replace the target string\n    foreach ($lines as &$line) {\n        if (strpos($line, $targetString) !== false) {\n            // Replace the rest of the line after the target string\n            $line = $targetString . $replacementString;\n        }\n    }\n\n    // Write the modified lines back to the file\n    $result = file_put_contents($filePath, implode("\n", $lines));\n\n    if ($result === false) {\n        throw new Exception("Failed to write to the file.");\n    }\n\n    return true;\n}\n\n// Example usage\n$filePath = 'path/to/your/file.txt';\n$targetString = '@parsethis';\n$replacementString = ' with this new content.';\n\ntry {\n    $success = overwriteLineInFile($filePath, $targetString, $replacementString);\n    echo "Line overwritten successfully.";\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}