// Generate token on the page making the request\n$token = bin2hex(random_bytes(32));\n$_SESSION['request_token'] = $token;\n\n// Pass token in AJAX request or form submission\n$ajaxData = array('token' => $token);\n\n// On the receiving page, verify the token\nif ($_SERVER['REQUEST_METHOD'] === 'POST' || $_SERVER['REQUEST_METHOD'] === 'GET') {\n    $receivedToken = $_POST['token'] ?? $_GET['token'];\n    if ($receivedToken !== $_SESSION['request_token']) {\n        // Invalid token, reject the request\n        http_response_code(403);\n        exit('Invalid request origin');\n    }\n    // Token is valid, proceed with the request\n}