$daysInMonth = date('t'); // get number of days in the current month\n$firstDayOfWeek = date('w', mktime(0, 0, 0, $month, 1, $year)); // get first day of the week (0 = Sunday, 1 = Monday, etc.)\n\n// generate the calendar header (day names)\necho "<tr>";\nfor ($i = 0; $i < 7; $i++) {\n    echo "<th>" . date('l', mktime(0, 0, 0, $month, $i, $year)) . "</th>";\n}\necho "</tr>";\n\n// generate the calendar rows\n$day = 1;\nfor ($i = 0; $i < 6; $i++) { // 6 rows, assuming the calendar will fit in 6 rows\n    echo "<tr>";\n    for ($j = 0; $j < 7; $j++) { // 7 columns, one for each day of the week\n        if ($i == 0 && $j < $firstDayOfWeek) {\n            echo "<td>&nbsp;</td>"; // blank cell if before the first day of the month\n        } elseif ($day > $daysInMonth) {\n            echo "<td>&nbsp;</td>"; // blank cell if after the last day of the month\n        } else {\n            // use the previous code to generate the cell with the day number\n            if ($day == $today && $month == date('n') && $year == date('Y')) {\n                echo "<td class='current-day'>$day</td>";\n            } else {\n                echo "<td class='row-calendar-cell'>$day</td>";\n            }\n            $day++;\n        }\n    }\n    echo "</tr>";\n}