// Get the session ID from the frontend application\n$sessionId = $_COOKIE['PHPSESSID'];\n\n// Open the session file in read-write mode\n$sessionFile = 'path/to/sessions/sess_' . $sessionId;\n$fp = fopen($sessionFile, 'r+');\nif ($fp) {\n    // Read the session data\n    $sessionData = unserialize(fread($fp, filesize($sessionFile)));\n\n    // Modify the session data\n    $sessionData['some_key'] = 'new_value';\n\n    // Write the updated session data back to the file\n    fseek($fp, 0);\n    fwrite($fp, serialize($sessionData));\n    fclose($fp);\n}