$today = date('j'); // get current day of the month\n$month = date('n'); // get current month\n$year = date('Y'); // get current year\n\n// assume you have a loop to generate the calendar cells\nfor ($i = 1; $i <= 31; $i++) {\n    // check if the current cell is today's date\n    if ($i == $today && $month == date('n') && $year == date('Y')) {\n        echo "<td class='current-day'>$i</td>";\n    } else {\n        echo "<td class='row-calendar-cell'>$i</td>";\n    }\n}