// Assume you have a function to fetch comments based on page number\nfunction get_comments($page_number) {\n    // Your database query to fetch comments for the given page number\n    // ...\n    return $comments;\n}\n\n// Handle AJAX request\nif (isset($_POST['page_number'])) {\n    $page_number = $_POST['page_number'];\n    $comments = get_comments($page_number);\n    echo json_encode($comments); // Output JSON encoded comments\n    exit;\n}