function get_comment_id($nid) {\n  // Load comments for the given node ID\n  $comments = comment_load_multiple(array('node_id' => $nid), array('status' => COMMENT_PUBLISHED));\n\n  // Check if any comments were found\n  if (!empty($comments)) {\n    // Get the first comment\n    $comment = reset($comments);\n    // Return the comment ID\n    return $comment->cid;\n  }\n\n  // If no comments were found, return NULL\n  return NULL;\n}