class Request {\n    // ...\n\n    public function getController() {\n        if ($this->_segment) {\n            // If a subdomain is present, use the StoreController\n            return 'StoreController';\n        } else {\n            // Otherwise, use the default controller\n            return DEFAULT_CONTROLLER;\n        }\n    }\n\n    public function getAction() {\n        if ($this->_segment) {\n            // If a subdomain is present, use the indexAction method\n            return 'indexAction';\n        } else {\n            // Otherwise, use the default action\n            return DEFAULT_ACTION;\n        }\n    }\n}