<?php\n\n// Load WordPress functions\nrequire_once 'path/to/wordpress/wp-load.php';\n\n// Define multiple functions to test\nfunction my_function_1($input) {\n    return 'Hello, ' . $input;\n}\n\nfunction my_function_2($input) {\n    return 'Goodbye, ' . $input;\n}\n\n// Create an interactive console\n$stdin = fopen('php://stdin', 'r');\n\nwhile (true) {\n    echo '> ';\n    $input = trim(fgets($stdin));\n\n    if ($input === 'exit') {\n        break;\n    }\n\n    $parts = explode(' ', $input);\n    $funcName = $parts[0];\n    $arg = implode(' ', array_slice($parts, 1));\n\n    if (function_exists($funcName)) {\n        $output = $funcName($arg);\n        echo $output . PHP_EOL;\n    } else {\n        echo "Unknown function: $funcName" . PHP_EOL;\n    }\n}\n\nfclose($stdin);\n\n?>