function getRandomStrength($quality, $min = 1.5, $max = 8.0) {\n    // Original interval\n    $originalMin = 1.1;\n    $originalMax = 9.9;\n    \n    // Generate value in the original interval\n    $rand = mt_rand()/mt_getrandmax();\n    $value = round(pow(M_E, ($rand - 1.033) / -0.45), 1);\n    \n    // Scale to the new interval\n    $scaledValue = $min + (($value - $originalMin) / ($originalMax - $originalMin)) * ($max - $min);\n    \n    return round($scaledValue, 1);\n}