// Staging site URL\n$staging_url = 'https://staging.example.com/wp-json/wp/v2/posts';\n\n// Production site URL\n$production_url = 'https://example.com/wp-json/wp/v2/posts';\n\n// Post ID to publish\n$post_id = 123;\n\n// Get the post data from the staging site\n$response = wp_remote_get($staging_url . '/' . $post_id);\n$post_data = json_decode($response['body'], true);\n\n// Publish the post to the production site\nwp_remote_post($production_url, array(\n    'method' => 'POST',\n    'headers' => array(\n        'Content-Type' => 'application/json',\n    ),\n    'body' => json_encode($post_data),\n));