function SaveXml() {\n    $fileName = $this->getFileName();\n    \n    // Header\n    $strFileData = '<?xml version="1.0" encoding="UTF-8" ?>' . "\n";\n    $strFileData .= '<rss version="2.0" xmlns:dc="http://purl.org/dc/elements/1.1/">' . "\n";\n    $strFileData .= '<channel>' . "\n";\n    $strFileData .= '<title>' . htmlspecialchars($this->getProjectName(), ENT_QUOTES, 'UTF-8') . '</title>' . "\n";\n    $strFileData .= '<link>http://www.mywebsite.com</link>' . "\n";\n    $strFileData .= '<description>My description</description>' . "\n";\n    $strFileData .= '<lastBuildDate>' . date("r") . '</lastBuildDate>' . "\n";\n    \n    // Data\n    foreach ($this->arrData as $value) {\n        $strFileData .= '<item>' . "\n";\n        $strFileData .= '<title>' . htmlspecialchars($value[0], ENT_QUOTES, 'UTF-8') . '</title>' . "\n";\n        $strFileData .= '<pubDate>' . htmlspecialchars($value[1], ENT_QUOTES, 'UTF-8') . '</pubDate>' . "\n";\n        $strFileData .= '<dc:creator>' . htmlspecialchars($value[2], ENT_QUOTES, 'UTF-8') . '</dc:creator>' . "\n";\n        $strFileData .= '<description><![CDATA[' . $value[3] . ']]></description>' . "\n";\n        $strFileData .= '<link><![CDATA[' . $value[4] . ']]></link>' . "\n";\n        $strFileData .= '<guid>' . htmlspecialchars($value[4], ENT_QUOTES, 'UTF-8') . '</guid>' . "\n";\n        $strFileData .= '<category>My category</category>' . "\n";\n        $strFileData .= '</item>' . "\n";\n    }\n    \n    $strFileData .= '</channel>' . "\n";\n    $strFileData .= '</rss>' . "\n";\n    \n    // Write to file\n    if (file_exists($fileName)) {\n        unlink($fileName);\n    }\n    \n    $fileHandler = fopen($fileName, 'w');\n    fwrite($fileHandler, $strFileData);\n    fclose($fileHandler);\n}