$str = preg_replace_callback('/<youtube=([^>]*)>/', function($match) {\n    $video_id = processYouTubeVideos($match[1]);\n    return '<object width="400" height="245">\n        <param name="movie" value="http://www.youtube-nocookie.com/v/' . $video_id . '&hl=en&fs=1&rel=0&showinfo=0">\n        <param name="allowFullScreen" value="true">\n        <param name="allowscriptaccess" value="always">\n        <embed src="http://www.youtube-nocookie.com/v/' . $video_id . '&hl=en&fs=1&rel=0&showinfo=0" type="application/x-shockwave-flash" allowscriptaccess="always" allowfullscreen="true" width="400" height="245">\n        </object>';\n}, $str);