function logUncaughtException($exception) {\n    // Create a log entry with a unique ID\n    $logEntry = array(\n        'timestamp' => date('Y-m-d H:i:s'),\n        'exception' => get_class($exception),\n        'message' => $exception->getMessage(),\n        'trace' => $exception->getTraceAsString(),\n        'id' => uniqid()\n    );\n\n    // Write the log entry to a file in a secure location\n    $logFile = '/path/to/secure/log/location/php-errors.log';\n    file_put_contents($logFile, json_encode($logEntry) . PHP_EOL, FILE_APPEND | LOCK_EX);\n\n    // Optionally send an alert to the development team\n    // ...\n\n    // Display a generic error message to the user\n    header('HTTP/1.1 500 Internal Server Error');\n    echo 'An unexpected error occurred.';\n    exit;\n}\n\nset_exception_handler('logUncaughtException');