$days = [\n    'Monday' => ['morning' => false, 'afternoon' => false],\n    'Tuesday' => ['morning' => false, 'afternoon' => false],\n    'Wednesday' => ['morning' => false, 'afternoon' => false],\n    'Thursday' => ['morning' => false, 'afternoon' => false],\n    'Friday' => ['morning' => false, 'afternoon' => false],\n    'Saturday' => ['morning' => false, 'afternoon' => false],\n    'Sunday' => ['morning' => false, 'afternoon' => false]\n];\n\n// Assuming you have a form with checkboxes like this:\n// <input type="checkbox" name="days[Monday][morning]" value="1">\n// <input type="checkbox" name="days[Monday][afternoon]" value="1">\n// ...\n\n// Get the posted values\n$postedDays = $_POST['days'];\n\n// Loop through the days and slots\nforeach ($days as $day => $slots) {\n    foreach ($slots as $slot => $value) {\n        if (isset($postedDays[$day][$slot])) {\n            $days[$day][$slot] = true;\n        }\n    }\n}