$connection = new COM('ADODB.Connection');\n$connection->Open('Provider=SQLOLEDB.1;Data Source=myServer;Initial Catalog=myDatabase;User ID=myUser;Password=myPassword');\n\n$query = 'SELECT * FROM myTable';\n$recordset = $connection->Execute($query);\n\nwhile (!$recordset->EOF) {\n    echo $recordset->Fields('myColumn')->value . "\n";\n    $recordset->MoveNext();\n}\n\n$recordset->Close();\n$connection->Close();